import React from 'react';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import NavbarRB from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import '../assets/css/navbar.css';

const CustomNavbar = () => {
  return (
    <NavbarRB expand="lg" className="navbar-custom">
      <Container fluid>
        <Row className="w-100 align-items-center">
          {/* Logo and Toggle for small screens */}
          <Col xs={6} lg={3} className="d-flex justify-content-start align-items-center">
            <NavbarRB.Brand href="#" className="navbar-brand-custom ms-3">Logo</NavbarRB.Brand>
            <NavbarRB.Toggle aria-controls="navbarScroll" className="ms-3" />
          </Col>

          {/* Centered Search Bar */}
          <Col xs={12} lg={6} className="d-flex justify-content-center my-2 my-lg-0">
            <Form className="d-flex w-75"> {/* Adjust width as needed */}
              <Form.Control
                type="search"
                placeholder="Search"
                className="me-2"
                aria-label="Search"
                style={{ width: '100%' }} // Makes sure it takes the full width of its container
              />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Col>

          {/* Navigation Links */}
          <Col xs={6} lg={3} className="d-flex justify-content-end">
            <Nav navbarScroll className="flex-row">
              <NavDropdown style="color : #fffff" title="Manage" id="navbarScrollingDropdown">
                <NavDropdown.Item href="#action3">Add New Book</NavDropdown.Item>
                <NavDropdown.Item href="#action4">Edit Book</NavDropdown.Item>
              </NavDropdown>
              <Nav.Link href="#action2" className="navbar-link-custom">Login</Nav.Link>
            </Nav>
          </Col>
        </Row>
      </Container>
    </NavbarRB>
  );
}

export default CustomNavbar;
