import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const categories = [
  { id: 1, name: "Fiction", color: "#FF6B6B", icon: "üìö" },
  { id: 2, name: "Non-Fiction", color: "#4ECDC4", icon: "üß†" },
  { id: 3, name: "Science", color: "#45B7D1", icon: "üî¨" },
  { id: 4, name: "History", color: "#F7B267", icon: "üèõÔ∏è" },
  { id: 5, name: "Biography", color: "#A06CD5", icon: "üë§" },
  { id: 6, name: "Children's", color: "#FF8CC8", icon: "üß∏" },
  { id: 7, name: "Fantasy", color: "#6BCB77", icon: "üêâ" },
  { id: 8, name: "Mystery", color: "#7D5BA6", icon: "üïµÔ∏è" },
];

function CategoryPage() {
  return (
    <Container fluid className="py-5" style={{ backgroundColor: '#f8f9fa' }}>
      <h1 className="text-center mb-5" style={{ color: '#333', fontWeight: 'bold' }}>Explore Our Book Categories</h1>
      <Row xs={1} sm={2} md={3} lg={4} className="g-4">
        {categories.map((category) => (
          <Col key={category.id}>
            <Card 
              className="h-100 shadow-sm" 
              style={{ 
                borderRadius: '15px', 
                transition: 'transform 0.2s',
                cursor: 'pointer'
              }}
              onMouseOver={e => e.currentTarget.style.transform = 'scale(1.05)'}
              onMouseOut={e => e.currentTarget.style.transform = 'scale(1)'}
            >
              <Card.Body className="d-flex flex-column justify-content-center align-items-center" style={{ backgroundColor: category.color, minHeight: '200px' }}>
                <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>{category.icon}</div>
                <Card.Title className="text-center" style={{ color: 'white', fontSize: '1.5rem' }}>{category.name}</Card.Title>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default CategoryPage;
